import { ProductRow } from "./ProductRow";

export function ProductCategoryRow(props) {

    props.data.sort((a, b) => {
        if (a.category > b.category) {
            return 1
        }
        if (a.category < b.category) {
            return -1
        }
        return 0
    })


    let currCategory = "";
    let categories = [];
    props.data.forEach(row => {
        if (row.category !== currCategory && props.onOnlyInStockChange(row) && props.onFilterTextChange(row)) {
            currCategory = row.category
            categories.push({ name: row.category })
        }
    })

    return (
        <>
            {categories.map((category, index) =>
                <ProductCategoryRowRender
                    key={index}
                    value={category.name}
                    data={props.data}
                    onOnlyInStockChange={props.onOnlyInStockChange}
                    onFilterTextChange={props.onFilterTextChange} />
            )}
        </>
    )
}

function ProductCategoryRowRender(props) {
    return (
        <>
            <tr>
                <th>{props.value}</th>
            </tr>
            <ProductRow category={props.value} data={props.data} onOnlyInStockChange={props.onOnlyInStockChange} onFilterTextChange={props.onFilterTextChange} />
        </>
    )
}
