const paginatorOffset = 2

export function ProductTableFooter(props) {
    const count = props.count
    const page = props.page
    const pageSize = props.pageSize
    const onPageChange = props.onPageChange

    let pagesArray = []

    pagesArray.push(
        <>
            <Paginator
                currentPage={page}
                pageTarget={1}
                onPageChange={onPageChange}
            />
        </>
    )

    let numberOfPages = Math.ceil(count / pageSize)
    for (let i = page - 2; i < numberOfPages; i++) {
        if (i < 1) {
            continue
        }
        if (i < page + 1) {
            pagesArray.push(
                <span
                    style={{ color: page === i + 1 ? "red" : "black" }}
                    onClick={e => onPageChange(i + 1)}> {i + 1}
                </span>
            )
        }
    }


    if (numberOfPages > pagesArray.length && page + 2 <= numberOfPages) {
        pagesArray.push(
            <>
                {page + 2 < numberOfPages && <span>...</span>}
                <span
                    onClick={e => onPageChange(numberOfPages)}> {numberOfPages}
                </span>
            </>
        )
    }

    return (
        <>
            {pagesArray}
        </>
    )
}

function Paginator(props) {
    const currentPage = props.currentPage
    const pageTarget = props.pageTarget
    const onPageChange = props.onPageChange
    return (
        <>
            <span
                style={{ color: currentPage === pageTarget ? "red" : "black" }}
                onClick={e => onPageChange(pageTarget)}> {pageTarget}
            </span>
            {currentPage - paginatorOffset > pageTarget && <span>...</span>}
        </>
    )
}
