export function ProductTableFooter(props) {
    const count = props.count
    const page = props.page
    const pageSize = props.pageSize
    const onPageChange = props.onPageChange

    let numberOfPages = Math.ceil(count / pageSize)
    let pagesArray = []
    for (let i = page; i < numberOfPages; i++) {
        let color = "black"
        if (i + 1 === page) {
            color = "red"
        }
        if (i < 3) {
            pagesArray.push(
                <span
                    style={{ color: color }}
                    onClick={e => onPageChange(i + 1)}> {i + 1}
                </span>
            )
        }
    }


    if (numberOfPages > pagesArray.length) {
        let color = "black"
        if (numberOfPages === page) {
            color = "red"
        }
        pagesArray.push(
            <>
                <span>...</span>
                <span
                    style={{ color: color }}
                    onClick={e => onPageChange(numberOfPages)}> {numberOfPages}
                </span>
            </>
        )
    }




    return (
        <>
            {pagesArray}
        </>
    )
}
