export function ProductTableFooter(props) {
    const count = props.count
    const page = props.page
    const pageSize = props.pageSize
    const onPageChange = props.onPageChange

    let pagesArray = []
    let color = "black"
    if (1 === page) {
        color = "red"
    }
    pagesArray.push(
        <>
            <span
                style={{ color: color }}
                onClick={e => onPageChange(1)}> {1}
            </span>
        </>
    )

    let numberOfPages = Math.ceil(count / pageSize)
    for (let i = page - 2; i < numberOfPages; i++) {
        if (i < 1) {
            continue
        }
        let color = "black"
        if (i + 1 === page) {
            color = "red"
        }
        if (i < page + 1) {
            pagesArray.push(
                <span
                    style={{ color: color }}
                    onClick={e => onPageChange(i + 1)}> {i + 1}
                </span>
            )
        }
    }


    if (numberOfPages > pagesArray.length - 1) {
        let color = "black"
        if (numberOfPages === page) {
            color = "red"
        }
        pagesArray.push(
            <>
                <span>...</span>
                <span
                    style={{ color: color }}
                    onClick={e => onPageChange(numberOfPages)}> {numberOfPages}
                </span>
            </>
        )
    }




    return (
        <>
            {pagesArray}
        </>
    )
}
