import { useEffect, useRef, useState } from "react"

export function ProductTableHeader(props) {

    const nameRef = useRef()
    const priceRef = useRef()

    const [nameColumn, setNameColumn] = useState("Name ↓")
    const [priceColumn, setPriceColumn] = useState("Price ($)")

    useEffect(() => {
        nameRef.current = nameColumn
    })

    function handleNameColumnChange() {
        if (nameColumn === "Name ↓") {
            setNameColumn("Name ↑")
        } else {
            setNameColumn("Name ↓")
        }
        setPriceColumn("Price ($)")
    }

    const order = props.order
    const orderDirection = props.orderDirection

    var currentOrder = order
    function handleOrderChange(e) {
        if (currentOrder === e.target.id) {
            if (orderDirection === "asc") {
                props.onOrderDirectionChange("desc")
            } else {
                props.onOrderDirectionChange("asc")
            }
        } else {
            props.onOrderChange(e.target.id)
            props.onOrderDirectionChange("asc")
        }
    }

    return (
        <tr>
            <th ref={nameRef} id="name" onClick={e => { handleNameColumnChange(); handleOrderChange(e); }}>{nameColumn}</th>
            <th ref={priceRef} id="price" onClick={handleOrderChange}>{priceColumn}</th>
        </tr>
    )
}
