import { ProductRow } from "./ProductRow";

export function ProductCategoryRow(props) {

    props.data.sort((a, b) => {
        if (a.category > b.category) {
            return 1
        }
        if (a.category < b.category) {
            return -1
        }
        return 0
    })


    let currCategory = "";
    let categories = [];
    props.data.forEach(row => {
        if (row.category !== currCategory) {
            currCategory = row.category
            categories.push({ name: row.category })
        }
    })

    return (
        <>
            {categories.map((category, index) =>
                <ProductCategoryRowHeader value={category.name} />
            )}
        </>
    )

}

function ProductCategoryRowHeader(props) {
    return (
        <>
            <tr>
                <th>{props.value}</th>
            </tr>
            <ProductRow />
        </>
    )
}
