export function ProductTableFooter(props) {
    const count = props.count
    const page = props.page
    const pageSize = props.pageSize
    const onPageChange = props.onPageChange

    let pagesArray = []
    for (let i = 0; i < Math.ceil(count / pageSize); i++) {
        let color = "black"
        if (i + 1 === page) {
            color = "red"
        }
        if (i < 3) {
            pagesArray.push(
                <span
                    style={{ color: color }}
                    onClick={e => onPageChange(i + 1)}> {i + 1}
                </span>
            )
        } else {
            let color = "black"
            if (pagesArray.length + 1 === page) {
                color = "red"
            }
            pagesArray.push(
                <>
                    <span>...</span>
                    <span
                        style={{ color: color }}
                        onClick={e => onPageChange(pagesArray.length + 1)}> {pagesArray.length + 1}
                    </span>
                </>
            )
        }
    }




    return (
        <>
            {pagesArray}
        </>
    )
}
