import { ProductRow } from "./ProductRow";

export function ProductCategoryRow(props) {

    props.data.sort((a, b) => {
        if (a.category > b.category) {
            return 1
        }
        if (a.category < b.category) {
            return -1
        }
        return 0
    })


    let currCategory = "";
    let categoryData = [];
    props.data.forEach(row => {
        if (row.category !== currCategory) {
            currCategory = row.category
            categoryData.push({ name: row.category, key: row.category })
        }
    })

    return (
        <>
            {categoryData.map(category =>
                <>
                    <tr key={category.key}>
                        <th>{category.name}</th>
                    </tr>
                    <ProductRow />
                </>
            )}
        </>
    )

}
