import React, { useState } from 'react';

export function LoginForm() {
    const [credentials, setCredentials] = useState({ user: "", password: "" });

    function showState() {
        console.log(credentials)
    }

    function handleChange(event) {
        console.log(event)
    }


    return (
        <form onSubmit={showState}>
            <label>
                User:
                <input type="text" value={credentials.user} onChange={handleChange} />
            </label>
            <label>
                Password:
                <input type="password" value={credentials.password} onChange={() => setCredentials({ password: credentials.password })} />
            </label>
            <input type="submit" value="Send" />
        </form>
    );
}
